mapper.select:
SELECT r.*
FROM (
        SELECT rownum rn, a.*
        FROM (
                select P.*
                from products P join likes L on (P.product_no = L.product_no)
                where user_id = #{userId}
            ) a

           WHERE rownum <= (#{page} * 12)
        ) r

WHERE r.rn > (#{page} - 1) * 12

mapper.update:
update qna
set title = #{title}, content = #{content}
where board_no = #{boardNo}

mapper.select:
select board_pw
from qna
where board_no = #{boardNo}

mapper.delete:
delete from likes 
where product_no = #{productNo} 
and user_id = #{userId}

mapper.select:
select count(1)
from qna
where user_id = #{userId}

select count(1)
from orders
where user_id = #{userId}

select count(1)
from likes
where user_id = #{userId}
