mapper.select:
select *
from users
where user_id = #{id}
and password = #{pw}

select user_id
from users
where user_name = #{userName}
and user_Tel = #{userTel}

select password
from users
where user_id = #{userId}
and user_Tel = #{userTel}
and user_name = #{userName}

mapper.delete:
delete from users
where user_id = #{userId}
and password = #{passWord}
